mapper.select:
SELECT * FROM
BGTCD_TERM
where CO_CD = #{coCd}

SELECT Multi_Num, BGT_CD ,Multi_Ck
FROM BGTCD
WHERE
parentCd=#{bgtCd} AND CO_CD=#{coCd} AND GROUP_CD=#{groupCd}
ORDER BY Multi_Num DESC
LIMIT 1;

SELECT BGT_CD , BGT_NM FROM BGTCD
WHERE CO_CD = #{coCd}
AND BGT_CD LIKE CONCAT('%', #{keyword}, '%');

SELECT * FROM
GISU
WHERE CO_CD=#{coCd};

SELECT * FROM
BGTGR
WHERE CO_CD=#{coCd} 
AND  BGTGR_CD LIKE CONCAT('%', #{keyword}, '%');

SELECT * FROM 
BGTCD
WHERE CO_CD=#{coCd}
and   GR_FG=#{grFg}
and   GISU =#{gisu}
and parentCd is NULL;

mapper.update:
UPDATE BGTCD
CTL_FG=#{ctlFg}, BGAJUST_FG=#{bgajustFg}, TO_DT=#{toDt}, BOTTOM_FG=#{bottomFg}, BIZ_FG=#{bizFg},
WHERE bgt_Cd =#{bgtCd}

UPDATE BGTCD_TERM
SET
DEF_NM=#{defNm}
WHERE
CO_CD=#{coCd} and DIV_FG=#{divFg}

UPDATE BGTCD
SET
BGT_NM=#{bgtNm}
WHERE
CO_CD=#{coCd} and BGT_CD=#{bgtCd}

mapper.select:
SELECT BGTCD.GISU, BGTGR.BGTGR_NM, BGTCD.BGT_CD AS BGT_CD, BGTCD.BGT_NM, ( SELECT GROUP_CONCAT(BGT_NM SEPARATOR ' > ') FROM BGTCD bgtcd_sub WHERE FIND_IN_SET(bgtcd_sub.BGT_CD, BGTCD.DATAPATH) ) AS DATA_PATH FROM BGTCD LEFT JOIN BGTGR ON BGTCD.GROUP_CD = BGTGR.BGTGR_CD WHERE BGTCD.CO_CD = #{coCd}
AND BGTCD.GISU = #{gisu}
AND BGTCD.TO_DT <= #{toDt} AND BGTCD.TO_DT >= SYSDATE()
AND bgtcd.GROUP_CD IN
#{item}
AND ( BGTCD.BGT_CD LIKE CONCAT('%', #{keyword}, '%') OR BGTCD.BGT_NM LIKE CONCAT('%', #{keyword}, '%') )

mapper.delete:
DELETE FROM BGTCD
WHERE
bgt_Cd = #{bgtCd} AND CO_CD=#{coCd}

mapper.select:
SELECT DISTINCT bgtcd.co_cd, bgtcd.GISU, bgtcd.gr_fg, bgtcd.bottom_fg, bgtcd.bgt_cd, bgtcd.GROUP_CD, bgtcd.DIV_FG, CASE WHEN BGTCD.div_Fg BETWEEN '1' AND '8' THEN ( SELECT BgtCD_Term.def_Nm FROM BgtCD_Term WHERE BgtCD_Term.co_Cd = BGTCD.co_Cd AND BgtCD_Term.div_Fg = BGTCD.div_Fg ) ELSE BGTCD.div_Fg END AS DEF_NM, bgtcd.BGT_NM, bgtcd.datapath, bgtcd.GROUP_CD, bgticf.DIV_CD FROM bgtcd LEFT JOIN bgticf ON bgticf.co_cd = bgtcd.co_cd WHERE bgtcd.CO_CD = #{coCd} AND BGTCD.GISU = #{gisu}
AND bgtcd.GR_FG = #{grFg}
AND bgtcd.GROUP_CD IN
#{item}
AND (bgticf.DIV_CD = #{divCd} OR bgticf.DIV_CD IS NULL)
ORDER BY bgtcd.group_cd, bgtcd.gr_fg, bgtcd.datapath, bgtcd.bgt_cd;

SELECT DISTINCT bgtcd.CO_CD, bgtcd.GISU, BGTCD.BGT_NM, BGTCD.BGT_CD, 1 AS LEVEL, CASE WHEN BGTCD.div_Fg BETWEEN '1' AND '8' THEN ( SELECT BgtCD_Term.def_Nm FROM BgtCD_Term WHERE BgtCD_Term.co_Cd = BGTCD.co_Cd AND BgtCD_Term.div_Fg = BGTCD.div_Fg ) ELSE BGTCD.div_Fg END AS DEF_NM, bgtcd.DIV_FG, bgticf.DIV_CD, BGTCD.BOTTOM_fG, bgtcd.group_cd, bgtcd.GR_FG FROM BGTCD JOIN BGTICF ON BGTCD.CO_CD = BGTICF.CO_CD WHERE bgtcd.CO_CD = #{coCd}
AND bgticf.DIV_CD = #{divCd}
AND bgtcd.GISU = #{gisu}
AND bgtcd.GROUP_CD IN
#{item}
AND bgtcd.GR_FG = #{grFg}
AND CONCAT(NVL(bgtcd.dataPath,''),bgtcd.bgt_cd,",") LIKE CONCAT( (SELECT CONCAT(NVL(bgtcd.dataPath,''),bgtcd.bgt_cd,",") FROM bgtCD WHERE BGT_CD = #{bgtCd}), "%");
