mapper.select:
SELECT DEF_NM FROM 
BGTCD_TERM
WHERE CO_CD=#{coCd} AND DIV_FG=#{divFg}

mapper.update:
UPDATE BGTCD
CTL_FG=#{ctlFg}, BGAJUST_FG=#{bgajustFg}, TO_DT=#{toDt}, BOTTOM_FG=#{bottomFg}, BIZ_FG=#{bizFg},
WHERE bgt_Cd =#{bgtCd}

UPDATE BGTCD_TERM
SET DEF_NM=#{defNm}
WHERE
CO_CD=#{coCd} and DIV_FG=#{divFg}

mapper.select:
	SELECT *
	FROM BGTCD
	WHERE CO_CD = #{coCd} 
	AND TO_DT > #{toDt} 
	AND GROUP_CD = #{groupCd}

        AND (
            BGT_CD LIKE CONCAT('%', #{keyword}, '%')
            OR
            BGT_NM LIKE CONCAT('%', #{keyword}, '%')
        )
