mapper.select:
SELECT DEF_NM FROM 
BGTCD_TERM
WHERE CO_CD=#{coCd} AND DIV_FG=#{divFg}

mapper.update:
UPDATE BGTCD
CTL_FG=#{ctlFg}, BGAJUST_FG=#{bgajustFg}, TO_DT=#{toDt}, BOTTOM_FG=#{bottomFg}, BIZ_FG=#{bizFg},
WHERE bgt_Cd =#{bgtCd}

UPDATE BGTCD_TERM
SET DEF_NM=#{defNm}
WHERE
CO_CD=#{coCd} and DIV_FG=#{divFg}

mapper.select:
	SELECT *
	FROM BGTCD
	LEFT JOIN BGTGR ON BGTCD.GROUP_CD = BGTGR.BGTGR_CD
	WHERE BGTCD.CO_CD = #{coCd} 

    AND BGTCD.GISU = #{gisu}

	AND BGTCD.TO_DT >= #{toDt} 

		AND BGTCD.GROUP_CD = #{groupCd}


        AND (
            BGTCD.BGT_CD LIKE CONCAT('%', #{keyword}, '%')
            OR
            BGTCD.BGT_NM LIKE CONCAT('%', #{keyword}, '%')
        )
