mapper.select:
SELECT * 
FROM CO 

	WHERE CO_CD = #{coCd}

ORDER BY INSERT_DT ASC;

mapper.update:
UPDATE CO SET 
CO_CD =#{coCd}, CO_NM = #{coNm},GISU = #{gisu}, FR_DT = #{frDt}, TO_DT = #{toDt}, JONGMOK = #{jongmok}, BUSINESS_TYPE = #{businessType}, CO_NB = #{coNb}, CEO_NM = #{ceoNm}, 
CO_ZIP = #{coZip}, CO_ADDR = #{coAddr}, CO_ADDR1 = #{coAddr1}
WHERE CO_CD=#{coCd}
