mapper.select:
SELECT * FROM PJT
WHERE
pjt_CD = #{pjtCd}
AND
co_CD = #{coCd}

mapper.update:
UPDATE PJT
SET
pgr_CD = #{pgrCd}, pgr_NM = #{pgrNm}, pjt_NM = #{pjtNm}, pr_DT = #{prDt}, to_DT = #{toDt}, prog_FG = #{progFg}, apjt_NM = #{apjtNm}, start_DT = #{stDt}
WHERE co_CD = #{coCd} AND pjt_CD = #{pjtCd}
